export const fetchCompany = id => {
  return fetch(`http://localhost:1337/companies/${id}`, {
    headers: {
      authorization: `Bearer ${process.env.REACT_APP_STRAPI_TOKEN}`
    }
  })
    .then(res => res.json())
    .then(json => {
      dispatch(_fetchProductsSuccess(json)); 
    })
};

export const _fetchCompanySuccess = companies => ({
  type: 'FETCH_COMPANY',
  payload: { companies }
});
